mapper.select:
select
  id, uid, video_id, created_at, updated_at, deleted_at
from favorite


        and id = #{id}


        and uid = #{uid}


        and video_id = #{videoId}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


        and deleted_at = #{deletedAt}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from favorite


        and id = #{id}


        and uid = #{uid}


        and video_id = #{videoId}


        and created_at = #{createdAt}


        and updated_at = #{updatedAt}


        and deleted_at = #{deletedAt}
